CREATE TABLE ProductSpecification (
  productNumber VARCHAR(5000) NOT NULL REFERENCES Product,
  name VARCHAR(200) NOT NULL,
  value VARCHAR(5000),
  PRIMARY KEY (productNumber, name)
);

